      "description": "ex) event_parameters={\"amount\":\"10000\"}",
      "uniqueItems": true,
      "items": {
        "description": "JSON 파라미터에 해당하는 조건 추가",
        "type": "object",
        "properties": {
          "event_type": {
            "title": "이벤트 타입",
            "required": true,
            "type": "string",
            "enum": [
              "value",
              "range",
              "not_allowed_value"
            ]
          },
          "key": {
            "type": "string",
            "title": "이벤트 이름",
            "description": "대상 이벤트 명",
            "required": true
          },
          "value": {
            "type": "object",
            "properties": {
              "value": {
                "type": "string",
                "title": "value",
                "maxLength": 41000
              },
              "from": {
                "type": "string",
                "title": "from"
              },
              "to": {
                "type": "string",
                "title": "to"
              }
            }
          }
        }
      }
    },
    "parameter_conditions": {
      "type": "array",
      "title": "완료 이벤트 조건 (개별 파라미터)",
      "description": "ex) amount=10000",
      "uniqueItems": true,
      "items": {
        "description": "개별 파라미터에 해당하는 조건 추가",
        "type": "object",
        "properties": {
          "event_type": {
            "title": "파라미터 타입",
            "required": true,
            "type": "string",
            "enum": [
              "value",
              "range",
              "not_allowed_value"
            ]
          },
          "key": {
            "type": "string",
            "title": "파라미터 이름",
            "description": "파라미터 명",
            "required": true
          },
          "value": {
            "type": "object",
            "properties": {
              "value": {
                "type": "string",
                "title": "value",
                "maxLength": 41000
              },
              "from": {
                "type": "string",
                "title": "from"
              },
              "to": {
                "type": "string",
                "title": "to"
              }
            }
          }
        }
      }
    },
    "min_payment_amount": {
      "title": "최소 결제 금액",
      "description": "리워드 지급/회수 기준에 사용될 최소 결제 금액을 입력하세요. (고정 리워드 광고 전용이며 취소 포스트백 연동 광고주인 경우만 사용 가능)",
      "type": "string",
      "format": "number_with_comma",
      "required": false
